import React, { useState, useEffect } from "react";
import Button from 'react-bootstrap/Button'
import { useHistory, useParams } from "react-router-dom";
import * as FirestoreService from '../services/firebase/firestore';
import Form from 'react-bootstrap/Form'
import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore';

function LobbyStart() {
    let history = useHistory();
    const [user] = useAuthState(FirestoreService.auth);
    let {code} = useParams();
    console.log(code);

    // useEffect(() => {
    // const [query, setQuery] = useState("");
    // const something = async() => {
    //     const q = await FirestoreService.db.collection('lobby').where("code", "==", code);
    //     setQuery(q);
    // }
    useEffect(() => {
        // console.log(FirestoreService.verifyGameCode(code))
        // if(FirestoreService.verifyGameCode(code)){
        //     if(!user){
                setTimeout(function(){
                    console.log(user)
                    if(user){
                        console.log("Welcome!")
                    } else {
                        history.push("/");
                    } }, 2000);
        //     }
        // }
    });
    const query = FirestoreService.db.collection('lobby').where("code", "==", code);
    const [game, loading] = useCollectionData(query);


    return (<div>
        <div>
            <h1>Code</h1>
        </div>
        <div>
            <h2>Players</h2>
            <div>
                {!loading && <PlayerList game={game}/> }
            </div>
        </div>
        </div>
        );
}

function PlayerList(props){
    const list = props.game[0].playerList;
    // console.log(list);
    const playerlist = list.map((player) => <h1>{player.name}</h1>);
    return (<div>{playerlist}</div>);
}

export default function Lobby() {
    const [user] = useAuthState(FirestoreService.auth);

    return (

        <div className="App">
    <header className="App-header">
        <h3>Hanabi</h3>
    </header>
    <body  className="App-body">
        <div>
            {user && <LobbyStart /> }
            {!user && <h1> User has not loaded</h1> }
        </div>
    </body>
    </div>
    );
}
