import React, { useState } from "react";
import Button from 'react-bootstrap/Button'
import { useHistory } from "react-router-dom";
import * as FirestoreService from '../services/firebase/firestore';
import Form from 'react-bootstrap/Form'

export default function Home() {
    let history = useHistory();

    const [gameLobby, setGameLobby] = useState({name:null, lobby:null, submit: false});
    const handleUsername = (username) =>{
        if (username.trim() === ""){
            let temp = {...gameLobby, name: null}
            setGameLobby(temp);
        } else {
            let temp = {...gameLobby, name: username.trim()}
            setGameLobby(temp);
        }
    }
    const handleGameCode = (code) => {
        if (username.trim() === ""){
            let temp = {...gameLobby, lobby: null}
            setGameLobby(temp);
        } else {
            let temp = {...gameLobby, lobby: code}
            setGameLobby(temp);
        }
    }
    const handleCreateLobby = () => {
        console.log("Create Lobby worked");
        console.log();
        // history.push("/about");
    }
    const handleJoinGame = async() => {
        console.log("Join Game Worked");
        FirestoreService.createLobby();
        // history.push("/users");
    }
    return (
    <div className="App">
    <header className="App-header">
        <h3>Hanabi</h3>
    </header>
    <body  className="App-body">
        <Form>
            <Form.Group controlId="ControlInput1">
                <Form.Label>Username</Form.Label>
                <Form.Control type="name" placeholder="Type Here..." onChange={(e) => handleUsername(e.target.value)}/>
            </Form.Group>
            <Form.Group controlId="ControlInput2">
                <Form.Label>Game Code</Form.Label>
                <Form.Control type="name" placeholder="Type Here..." onChange={(e) => handleGameCode(e.target.value)} />
            </Form.Group>
        </Form>
        <div className="Page-home-buttons">
            <Button variant="outline-primary" size="lg" onClick={handleCreateLobby}>Create Lobby</Button>
            <span className="Spacer"/>
            <Button variant="outline-info"  size="lg" onClick={handleJoinGame}>Join Game</Button> 
        </div>  
    </body>
    </div>);
  }


