import React, { useState, useEffect } from "react";
import Button from 'react-bootstrap/Button'
import { useHistory, useParams } from "react-router-dom";
import * as FirestoreService from '../services/firebase/firestore';
import Form from 'react-bootstrap/Form'
import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore';

export default function Lobby() {
    const [user] = useAuthState(FirestoreService.auth);
    let {code} = useParams();
    console.log(code);

    // useEffect(() => {
    const [query, setQuery] = useState(FirestoreService.auth);
    const something = async() => {
        const q = await FirestoreService.db.collection('lobby').get(code);
        setQuery(q);

    }

    const [game, loading] = useCollectionData(query, {idField:'id'});
    console.log(loading);


    return (
    <div className="App">
    <header className="App-header">
        <h3>Hanabi</h3>
    </header>
    <body  className="App-body">
        <div>
            <h1>Code</h1>
        </div>
        <div>
            <h2>Players</h2>
            <div>
                {!loading && <PlayerList game={game}/> }
            </div>
        </div>
    </body>
    </div>);
}

