import React, { useState } from "react";
import Button from 'react-bootstrap/Button'
import { useHistory } from "react-router-dom";
import * as FirestoreService from '../services/firestore';
import * as GameCodeGenerator from '../tools/generator/GameCodeGenerator'

export default function Home() {
    let history = useHistory();
    const handleCreateLobby = () => {
        console.log("Create Lobby worked");
        console.log(GameCodeGenerator.GameCodeGenerator())
        // history.push("/about");
    }
    const handleJoinGame = () => {
        console.log("Join Game Worked");
        FirestoreService.db.collection('lobby').get().then((querySnapshot) => {
            querySnapshot.forEach((doc) => {
                console.log(`${doc.id} => ${doc.data()}`);
            });
        });
        // history.push("/users");
    }
    return (
    <div className="App">
    <header className="App-header">
        <h3>Hanabi</h3>
    </header>
    <body  className="App-body">
        <div className="Page-home-buttons">
            <Button variant="outline-primary" size="lg" onClick={handleCreateLobby}>Create Lobby</Button>
            <span className="Spacer"/>
            <Button variant="outline-info"  size="lg" onClick={handleJoinGame}>Join Game</Button> 
        </div>  
    </body>
    </div>);
  }
